mapper.select:
SELECT * FROM Theme


		themegenre = #{themegenre}



ORDER BY picture asc

mapper.insert:
insert into theme(seq, themename, themegenre, thlevel, runtime,
thcost, peoplecount, branch, content

	, picture, b_fsize

)

values (Theme_seq.nextval, #{themename}, #{themegenre},#{thlevel},
#{runtime},#{thcost}, #{peoplecount}, #{branch}, #{content}

	, #{picture}, #{b_fsize}

)

mapper.update:
UPDATE theme SET
themename = #{themename}, themegenre = #{themegenre}, thlevel = #{thlevel}, runtime = #{runtime}, thcost = #{thcost}, peoplecount = #{peoplecount}, branch = #{branch}, content = #{content}
, picture = #{picture}
WHERE seq= #{seq}
